import BeigeCard from "../../layout/card/BeigeCard";
import { useSelector } from "react-redux";
import "./AllRestaurants.scss";
import { Link, useParams } from "react-router-dom";
import { useAppDispatch } from "../../../store/store";
import { useEffect, useState } from "react";
import { fetchRestaurants } from "../../../store/RestaurantDataSlice";

const RestaurantsGrid = ({ filter }: any) => {
  let {pageNumber}:any = useParams();
    if(!pageNumber) {
      pageNumber = 1;
    }

  const dispatch = useAppDispatch();
  const { restaurants } = useSelector((state: any) => state.restaurants);

  useEffect(() => {
    dispatch(fetchRestaurants());
  }, []);

  const passFilter = (item: any) => {
    switch (filter) {
      case "all":
        return true;
      case "new":
        if (item.isNewRestaurant) {
          return true;
        } else return false;
      case "popular":
        if (item.isPopular) {
          return true;
        } else return false;
      case "open":
        const opening = item.openingHour.split(":")[0] * 1;
        const closing = item.closingHour.split(":")[0] * 1;
        const date = new Date();
        const now = date.getHours();
        if (now >= opening && now <= closing) {
          return true;
        } else return false;
      default:
        return true;
    }
  };
  const newLength = restaurants.filter((item: any) => passFilter(item));
  const numberOfPages = Math.ceil(newLength.length / 9);
  let pages=[];
  for (let page = 1; page <= numberOfPages; page++) {
    pages.push( <Link key={page} className="page" to={`/restaurants/${page}`} onClick={pageNumber= page}> {page}</Link>)
  }


  return (
    <div>
      <div className="restaurants-grid">
        {newLength.length <= 9 &&
          newLength.map((item: any) => (
            <Link to={`/restaurant/${item.name}`} className="to-restaurant-btn">
              <BeigeCard
                key={item.name}
                title={item.name}
                detail={item.chef}
                img={item.mobileImage}
              ></BeigeCard>
            </Link>
          ))}
      </div>
      <div>
        {newLength.length > 9 && (
          <div>
            <div className="restaurants-grid">
              {newLength.slice((pageNumber * 9 - 9), (pageNumber * 9 -1 )).map((item: any) => (
                //0, 8 =>  9, 17
                <Link
                  to={`/restaurant/${item.name}`}
                  className="to-restaurant-btn"
                >
                  <BeigeCard
                    key={item.name}
                    title={item.name}
                    detail={item.chef}
                    img={item.mobileImage}
                  ></BeigeCard>
                </Link>
              ))}
            </div>
            <div className="pagination">{pages}</div>
          </div>
        )}
      </div>
    </div>
  );
};
export default RestaurantsGrid;
