import BeigeCard from "../../layout/card/BeigeCard";
import { useSelector } from "react-redux";
import "./AllRestaurants.scss";
import { Link } from "react-router-dom";
import { useAppDispatch } from "../../../store/store";
import { useEffect, useState } from "react";
import { fetchRestaurants } from "../../../store/RestaurantDataSlice";
import PageSelector from "./PageSelector";

const RestaurantsGrid = ({ filter }: any) => {
  const dispatch = useAppDispatch();
  const { restaurants } = useSelector((state: any) => state.restaurants);

  useEffect(() => {
    dispatch(fetchRestaurants());
  }, []);

  let [all, setAll]: any = useState(0);
  let [neww, setNew]: any = useState(0);
  let [popular, setPopular]: any = useState(0);
  let [open, setOpen]: any = useState(0);

  const [restaurantsLength, setRestaurantsLength]= useState(restaurants.length)

  console.log(restaurants.filter((item:any)=> passFilter(item)))

  const passFilter = (item: any) => {
    switch (filter) {
      case "all":
       // setAll(all++);
        return true;
      case "new":
        if (item.isNewRestaurant) {
         // setNew(neww++);
          return true;
        } else return false;
      case "popular":
        if (item.isPopular) {
         // setPopular(popular++);
          return true;
        } else return false;
      case "open":
        const opening = item.openingHour.split(":")[0] * 1;
        const closing = item.closingHour.split(":")[0] * 1;
        const date = new Date();
        const now = date.getHours();
        if (now >= opening && now <= closing) {
          setOpen(open++);
          return true;
        } else return false;
      default:
        return true;
    }
  };

  return (
    <div className="restaurants-grid">
      {restaurants.length <= 9 &&
        restaurants.map((item: any) => {
          if (passFilter(item))
            return (
              <Link
                to={`/restaurant/${item.name}`}
                className="to-restaurant-btn"
              >
                <BeigeCard
                  key={item.name}
                  title={item.name}
                  detail={item.chef}
                  img={item.mobileImage}
                ></BeigeCard>
              </Link>
            );
        })}
      {restaurants.length > 9 &&
        restaurants.map((item: any) => {
          if (passFilter(item))
            return (
              <Link
                to={`/restaurant/${item.name}`}
                className="to-restaurant-btn"
              >
                <BeigeCard
                  key={item.name}
                  title={item.name}
                  detail={item.chef}
                  img={item.mobileImage}
                ></BeigeCard>
              </Link>
            );
        })}

    </div>
  );
};
export default RestaurantsGrid;
