import BeigeCard from "../../layout/card/BeigeCard";
import { useSelector } from "react-redux";
import "./AllRestaurants.scss";
import { Link } from "react-router-dom";
import { useAppDispatch } from "../../../store/store";
import { Fragment, useEffect, useState } from "react";
import { fetchRestaurants } from "../../../store/RestaurantDataSlice";
import { fetchNewRestaurants } from "../../../store/newRestaurantsSlice";
import { fetchPopularRestaurants } from "../../../store/popularRestaurantsSlice";
import { fetchOpenRestaurants } from "../../../store/openRestaurantsSlice";
import { useParams } from "react-router-dom";
const RestaurantsGrid = ({ filter }: any) => {
  const [pageNumber, setPageNumber] = useState(1);

  const dispatch = useAppDispatch();
  const { restaurants } = useSelector((state: any) => state.restaurants);
  const { newRestaurants } = useSelector((state: any) => state.newRestaurants);
  const { popularRestaurants } = useSelector((state: any) => state.popularRestaurants);
  const { openRestaurants } = useSelector((state: any) => state.openRestaurants);

  useEffect(() => {
    dispatch(fetchRestaurants());
    dispatch(fetchNewRestaurants());
    dispatch(fetchPopularRestaurants());
    dispatch(fetchOpenRestaurants());
  }, []);

  const passFilter = (item: any) => {
    switch (filter) {
      case "all":
        return restaurants;
      case "new":
        if (item.isNewRestaurant) {
          return newRestaurants;
        } else return false;
      case "popular":
        if (item.isPopular) {
          return popularRestaurants;
        } else return false;
      case "open":
        const opening:number = item.openingHour.split(":")[0] * 1;
        const closing:number = item.closingHour.split(":")[0] * 1;
        const date= new Date();
        const now:number = date.getHours();
        if (now >= opening && now <= closing) {
          return openRestaurants;
        } else return false;
      default:
        return true;
    }
  };

 const filteredArr: object[] = restaurants.map((item:any)=> passFilter(item))
  const numberOfPages:number = Math.ceil(filteredArr.length / 6);
  let pages = [];
  for (let page = 1; page <= numberOfPages; page++) {
    pages.push(
      <Link
        key={page}
        className="page"
        to={`/restaurants/${page}`}
        onClick={() => setPageNumber(page)}>
        {page}
      </Link>
    );
  }

  return (
    <Fragment>
      <div className="restaurants-grid">
        {filteredArr.length <= 6 &&
          filteredArr.map((item: any) => (
            <Link to={`/restaurant/${item.name}`} className="to-restaurant-btn" key={item._id}>
              <BeigeCard
                key={item.name}
                title={item.name}
                detail={item.chef}
                img={item.mobileImage}
              ></BeigeCard>
            </Link>
          ))}
      </div>
      <Fragment>
        {filteredArr.length > 6 && (
          <Fragment>
            <div className="restaurants-grid">
              {filteredArr
                .slice(pageNumber * 6 - 6, pageNumber * 6)
                .map((item: any) => (
                  <Link
                    to={`/restaurant/${item.name}`}
                    className="to-restaurant-btn" key={item._id}
                  >
                    <BeigeCard
                      key={item.name}
                      title={item.name}
                      detail={item.chef}
                      img={item.mobileImage}
                    ></BeigeCard>
                  </Link>
                ))}
            </div>
            <div className="pagination">{pages}</div>
          </Fragment>
        )}
      </Fragment>
    </Fragment>
  );
};
export default RestaurantsGrid;
