import BeigeCard from "../../layout/card/BeigeCard";
import { useSelector } from "react-redux";
import "./AllRestaurants.scss";
import { Link } from "react-router-dom";
import { useAppDispatch } from "../../../store/store";
import { useEffect, useState } from "react";
import { fetchRestaurants } from "../../../store/RestaurantDataSlice";
import PageSelector from "./PageSelector";

const RestaurantsGrid = ({ filter }: any) => {
  const dispatch = useAppDispatch();
  const { restaurants } = useSelector((state: any) => state.restaurants);

  useEffect(() => {
    dispatch(fetchRestaurants());
  }, []);

  let [all, setAll]: any = useState(0);
  let [neww, setNew]: any = useState(0);
  let [popular, setPopular]: any = useState(0);
  let [open, setOpen]: any = useState(0);

  const [restaurantsLength, setRestaurantsLength] = useState(
    restaurants.length
  );

  const passFilter = (item: any) => {
    switch (filter) {
      case "all":
        // setAll(all++);
        return true;
      case "new":
        if (item.isNewRestaurant) {
          // setNew(neww++);
          return true;
        } else return false;
      case "popular":
        if (item.isPopular) {
          // setPopular(popular++);
          return true;
        } else return false;
      case "open":
        const opening = item.openingHour.split(":")[0] * 1;
        const closing = item.closingHour.split(":")[0] * 1;
        const date = new Date();
        const now = date.getHours();
        if (now >= opening && now <= closing) {
          // setOpen(open++);
          return true;
        } else return false;
      default:
        return true;
    }
  };
  const newLength = restaurants.filter((item: any) => passFilter(item));
const numberOfPages= Math.ceil(newLength.length/9)
 let pages:any = [];
for(let i=1; i< numberOfPages; i++) {
  <Link key={i} className="page" to={`/page/${i}`}>{i}</Link>
}

  return (
    <div>
      <div className="restaurants-grid">
        {newLength.length <= 9 &&
          newLength.map((item: any) => (
            <Link to={`/restaurant/${item.name}`} className="to-restaurant-btn">
              <BeigeCard
                key={item.name}
                title={item.name}
                detail={item.chef}
                img={item.mobileImage}
              ></BeigeCard>
            </Link>
          ))}
      </div>
      <div>
        {newLength.length > 9 && (
          <div>
            <div className="restaurants-grid">
              {newLength.map((item: any) => (
                <Link
                  to={`/restaurant/${item.name}`}
                  className="to-restaurant-btn"
                >
                  <BeigeCard
                    key={item.name}
                    title={item.name}
                    detail={item.chef}
                    img={item.mobileImage}
                  ></BeigeCard>
                </Link>
              ))}
            </div>
            <p>fdf</p>
          </div>
        )}
      </div>
    </div>
  );
};
export default RestaurantsGrid;
