import Header from "../../header/Header";
import "./AllRestaurants.scss";
import { useState } from "react";
import Footer from "../../footer/Footer";
import RestaurantsGrid from "./RestaurantsGrid";
import React from "react";

const AllRestaurants: React.FC = () => {
  const [filter, setFilter] = useState("all");
  return (
    <div className="all-restaurants-page">
      <Header />
      <br /> <br />
      <h2>Restaurants</h2>
      <div className="categories-div">
        <button onClick={() => setFilter("all")} className={`${filter === "all" ? 'all':''}`}>All</button>
        <button onClick={() => setFilter("new")} >New</button>        
        <button onClick={() => setFilter("popular")} className={filter}>Most Popular</button>
        <button onClick={() => setFilter("open")} className={filter}>Open Now</button>
      </div>
      <RestaurantsGrid filter={filter} />
      <hr />
      <Footer />
    </div>
  );
};

export default AllRestaurants;
